import {Field, Form, Formik} from "formik";
import React, {useEffect, useState} from "react";
import * as typeService from '../components/service/ProductTypeService'
import * as productService from '../components/service/ProductService'
import {useNavigate} from "react-router-dom";
import * as Swal from "sweetalert2";


export const CreateProduct = () => {
    const [type, setType] = useState([])
    const navigate = useNavigate()

    const showType = async () => {
        try {
            const res = await typeService.findAll()
            setType(res)
        } catch (e) {
            console.log(e)
        }
    }

    useEffect(() => {
        showType()
    }, [])


    return (

        <Formik
            initialValues={{
                name: '',
                price: '',
                type: ''
            }}
            onSubmit={(values) => {
                const res = async () => {
                    try {
                        await productService.save(values)
                        await navigate('/')
                        await Swal.fire({
                            title: 'Success',
                            icon: "success",
                            text: 'Created Successfully'
                        })
                    } catch (e) {
                        console.log(e)
                    }
                }
                res()
            }}>
            <h1>Create product</h1>
            <Form>
                <label>Name</label>
                <Field type='text' name='name'/>


                <label>Price</label>
                <Field type='number' name='price'/>


                <Field as='select' name='type'>
                    <option value="">--Select type--</option>
                    {
                        type && type.map((ty) => (
                            <option value={ty.id} key={ty.id}>{ty.name}</option>
                        ))
                    }
                </Field>


                <button type={"submit"}>Submit</button>

            </Form>

        </Formik>
    )
}
